import React, { useEffect } from 'react';
import { useAppDispatch, useAppSelector } from '../../app/hooks';
import {
  fetchRecettesAsync,
  selectRecettes,
  selectStatus,
  Statuses,
} from './recetteSlice';
import RecetteForm from './RecetteForm';
import Recette from './Recette';

function Recettes() {
    const posts = useAppSelector(selectRecettes);
    const status = useAppSelector(selectStatus)
    const dispatch = useDispatch();

    // const [postToEdit, setPostToEdit] = useState(0);

    useEffect(() => {
      dispatch(fetchRecettesAsync());
    }, [dispatch])

  let contents;

  if (status !== Statuses.UpToDate) {
    contents = <div>{status}</div>
  } else {
      contents = <div className="card">
        <div className="card-body">
            <h3>{status}</h3>
            <RecetteForm />
            {recettes && recettes.length > 0 && recettes.map(recette => {
                return <div key={recette.id} style={{margin:"5em"}}>
                    <Recette 
                        dispatch={dispatch}
                        recette={recette}
                        // toggleEditForm={() => toggleEditForm(recette.id)}
                        // recetteToEdit={recetteToEdit}
                        // submitEdit={submitEdit}
                    />
                </div>
            })}
            </div>
      </div>
  }

  return <div><h1>Recettes</h1>
        {contents}
  </div>
}

export default Recettes;
