import React, { useEffect } from 'react';
import { useAppDispatch, useAppSelector } from '../../app/hooks';
import {
  fetchRecettesAsync,
  selectRecettes,
  selectStatus,
  Statuses,
} from './recetteSlice';
import RecetteForm from './RecetteForm';

function Recettes() {
  const recettes = useAppSelector(selectRecettes);
  const status = useAppSelector(selectStatus);
  const dispatch = useAppDispatch();

  useEffect(() => {
    dispatch(fetchRecettesAsync());
  }, [dispatch]);

  let contents;

  if (status !== Statuses.UpToDate) {
    contents = <div>{status}</div>;
  } else {
    contents = <div className="card">
    <div className="card-body">
        <h3>{status}</h3>
        <RecetteForm/>
        {recettes && recettes.length > 0 && recettes.map(recette => {
            return <div key={recette.id} style={{margin:"5em"}}>
              <h3>{recette.titre}</h3>
              <p>{recette.ingredients}</p>
            </div>
   })}
        </div>
      </div>
  }


  return (
    <div><h1>Recettes</h1>{contents}</div>

  );
}

export default Recettes;
