import { createAsyncThunk, createSlice, PayloadAction } from '@reduxjs/toolkit';
import { RootState } from '../../app/store';
import { fetchRecettes } from './recetteAPI';

export enum Statuses {
  Initial = 'Not Fetched',
  Loading = 'Loading...',
  UpToDate = 'Up To Date',
  Deleted = 'Deleted',
  Error = 'Error'
}

export interface RecetteState {
  id?: number;
  titre?: string;
  ingredients?: string;
  created_at?: any;
  updated_at?: any;
}

export interface RecettesState {
  recettes: RecetteState[];
  status: Statuses;
}

const initialState: RecettesState = {
  recettes: [],
  status: Statuses.Initial
};

export const fetchRecettesAsync = createAsyncThunk(
  'recettes/fetchRecettes',
  async () => {
    const response = await fetchRecettes();
    return response;
  }
);

export const recetteSlice = createSlice({
  name: 'recettes',
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchRecettesAsync.pending, (state) => {
        state.status = Statuses.Loading;
      })
      .addCase(fetchRecettesAsync.fulfilled, (state, action: PayloadAction<RecetteState[]>) => {
        state.recettes = action.payload;
        state.status = Statuses.UpToDate;
      })
      .addCase(fetchRecettesAsync.rejected, (state) => {
        state.status = Statuses.Error;
      });
  }
});

export const selectRecettes = (state: RootState) => state.recettes.recettes;

export const selectStatus = (state: RootState) => state.recettes.status;

export default recetteSlice.reducer;
